import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';

interface AdminLoginModalProps {
  show: boolean;
  onClose: () => void;
  onLoginSuccess: (username: string) => void;
}

const AdminLoginModal: React.FC<AdminLoginModalProps> = ({ show, onClose, onLoginSuccess }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState<string | null>(null);

  const handleLogin = () => {
    // Simulating admin login, replace this with real authentication logic
    if (username === 'admin' && password === 'admin_password') {
      onLoginSuccess(username);
      onClose();
    } else {
      setError('Invalid admin credentials');
    }
  };

  return (
    <Modal show={show} onHide={onClose}>
      <Modal.Header closeButton>
        <Modal.Title>Admin Login</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="adminUsername">
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter admin username"
            />
          </Form.Group>
          <Form.Group controlId="adminPassword" className="mt-3">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter admin password"
            />
          </Form.Group>
          {error && <p className="text-danger mt-3">{error}</p>}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Cancel
        </Button>
        <Button variant="primary" onClick={handleLogin}>
          Login
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AdminLoginModal;
