import React, { useState, useEffect } from 'react';
import { FaStar } from 'react-icons/fa'; // Using FontAwesome for star icons

interface PriceComparison {
  store_name: string;
  price: number;
  product_link: string; // Assuming there's a product link field for each store
}

interface PricecompProps {
  productId: number | null;
  userId: number | null; // Assume you're passing the user ID as a prop
}

const Pricecomp: React.FC<PricecompProps> = ({ productId, userId }) => {
  const [priceComparisons, setPriceComparisons] = useState<PriceComparison[]>([]);
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [favoriteProductId, setFavoriteProductId] = useState<number | null>(null); // Store the favorite product ID

  useEffect(() => {
    if (productId) {
      setLoading(true);
      setError(null);
      
      fetch(`http://localhost:5000/product-prices/${productId}`)
        .then((response) => {
          if (!response.ok) {
            throw new Error('Failed to fetch price comparisons');
          }
          return response.json();
        })
        .then((data: PriceComparison[]) => {
          // Sort data by price in ascending order (cheapest first)
          const sortedData = data.sort((a, b) => a.price - b.price);
          setPriceComparisons(sortedData);
          setLoading(false);
        })
        .catch((error) => {
          setError(error.message);
          setLoading(false);
        });
    }
  }, [productId]);

  const handleFavorite = async (productId: number) => {
    if (!userId) {
      alert("You need to log in to add favorites!");
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/favorites`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ user_id: userId, product_id: productId }),
      });

      if (!response.ok) {
        throw new Error('Failed to add favorite');
      }

      setFavoriteProductId(productId); // Mark this product as favorite
      alert('Product added to favorites!');
    } catch (error) {
      console.error('Error adding to favorites:', error);
    }
  };

  if (loading) {
    return (
      <div className="container d-flex justify-content-center align-items-center">
        <p>Loading price comparisons...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container d-flex justify-content-center align-items-center">
        <p>Error: {error}</p>
      </div>
    );
  }

  if (!priceComparisons.length) {
    return (
      <div className="container d-flex justify-content-center align-items-center">
        <p>No price comparisons available for this product.</p>
      </div>
    );
  }

  // Get the cheapest price for percentage calculation
  const cheapestPrice = priceComparisons[0].price;

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-12">
          <table className="table table-dark">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Store Name</th>
                <th scope="col">Price</th>
                <th scope="col">Price Comparison (%)</th>
                <th scope="col">Product Link</th>
                <th scope="col">Favorite</th>
              </tr>
            </thead>
            <tbody>
              {priceComparisons.map((comparison, index) => (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td>{comparison.store_name}</td>
                  <td>{Number(comparison.price).toFixed(2)} ₽</td>
                  <td>
                    {(comparison.price / cheapestPrice * 100).toFixed(2)}%
                  </td>
                  <td>
                    <a href={comparison.product_link} target="_blank" rel="noopener noreferrer" className="text-white">
                      View Product
                    </a>
                  </td>
                  <td>
                    <FaStar
                      onClick={() => handleFavorite(productId!)} // Add to favorites
                      color={favoriteProductId === productId ? 'gold' : 'white'}
                      style={{ cursor: 'pointer' }}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Pricecomp;
