import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

export interface SearchResult {
  id: number;
  name: string;
  description: string;
  image_url: string;
}

interface SearchbarProps {
  onSelect: (product: SearchResult) => void; // Callback for selecting a product
}

const Searchbar: React.FC<SearchbarProps> = ({ onSelect }) => {
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);

  const handleSearch = async (): Promise<void> => {
    if (!searchTerm) return;

    try {
      const response = await fetch(`http://localhost:5000/search?q=${searchTerm}`);
      const data: SearchResult[] = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  // Add a function to handle the "Enter" key press
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>): void => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className="searchbar">
      <section id="searchsect" className="my-5 b">
        <div className="container my-1">
          <div className="row">
            <div className="col-md-9">
              <div className="input-group">
                <input
                  type="search"
                  className="form-control rounded"
                  placeholder="Search"
                  aria-label="Search"
                  aria-describedby="search-addon"
                  value={searchTerm}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)}
                  
                />
                <button type="button" className="btn btn-outline-primary" onClick={handleSearch}>
                  Search
                </button>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-md-9">
              <ul className="list-group">
                {searchResults.map((result) => (
                  <li
                    key={result.id}
                    className="list-group-item"
                    onClick={() => onSelect(result)} // Call onSelect when a result is clicked
                    style={{ cursor: 'pointer' }}
                  >
                    {result.name}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Searchbar;
