import React from 'react';
import { FaStar } from 'react-icons/fa';

interface Product {
  id: number;
  name: string;
  description: string;
  image_url: string;
}

interface User {
  id: number;
  username: string;
}

interface ContentProps {
  selectedProduct: Product | null;
  loggedInUser: User | null; // Add loggedInUser as a prop
}

const Content: React.FC<ContentProps> = ({ selectedProduct, loggedInUser }) => {
  const handleAddToFavorites = async () => {
    if (!selectedProduct || !loggedInUser) return;

    try {
      const response = await fetch('http://localhost:5000/favorites', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user_id: loggedInUser.id,
          product_id: selectedProduct.id,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to add to favorites');
      }

      alert('Product added to favorites!');
    } catch (error) {
      console.error('Error adding to favorites:', error);
    }
  };

  return (
    <div className="container">
      {selectedProduct ? (
        <section id="product-details" className="my-5 b">
          <div className="container my-1">
            <div className="row justify-content-center align-items-center">
              <div className="col-md-2">
                <img
                  src={selectedProduct.image_url || 'https://placehold.co/600x400'}
                  loading="lazy"
                  alt={selectedProduct.name}
                  className="img-fluid"
                />
              </div>
              <div className="col-md-6">
                <article>
                  <h2>{selectedProduct.name}</h2>
                  <p>{selectedProduct.description}</p>
                </article>
                {loggedInUser && (
                  <button onClick={handleAddToFavorites} className="btn btn-primary mt-3">
                    <FaStar /> Add to Favorites
                  </button>
                )}
              </div>
            </div>
          </div>
        </section>
      ) : (
        <div className="row d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
          <div className="col-md-6 text-center">
            <p>Please select a product to view details.</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Content;
