import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

interface FavoriteProduct {
  id: number;
  name: string;
  description: string;
  image_url: string;
}

interface FavoritesProps {
  userId: number | null;
}

const Favorites: React.FC<FavoritesProps> = ({ userId }) => {
  const [favorites, setFavorites] = useState<FavoriteProduct[]>([]);

  useEffect(() => {
    if (userId) {
      fetchFavorites(userId);
    }
  }, [userId]);

  const fetchFavorites = async (userId: number) => {
    try {
      const response = await fetch(`http://localhost:5000/favorites/${userId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch favorites');
      }
      const data = await response.json();
      setFavorites(data);
    } catch (error) {
      console.error('Error fetching favorites:', error);
    }
  };

  return (
    <div className="sticky-favorites-container">
      <div className="container">
        <div className="row">
          <div className="col-md-8">
            {/* Main content */}
          </div>

          <div className="col-md-4">
            <div className="sticky-favorites">
              <h3>Избранное</h3>
              {favorites.length > 0 ? (
                <ul>
                  {favorites.map((product) => (
                    <li key={product.id}>
                      <img src={product.image_url} alt={product.name} width="50" />
                      <span>{product.name}</span>
                    </li>
                  ))}
                </ul>
              ) : (
                <p>Ваш список избранного пуст</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Favorites;
