import React, { useEffect, useState } from 'react';

interface Price {
  store_name: string;
  price: number;
  link: string;
}

interface PricecompProps {
  selectedProductId: number | null;
}

const Pricecomp: React.FC<PricecompProps> = ({ selectedProductId }) => {
  const [prices, setPrices] = useState<Price[]>([]);

  useEffect(() => {
    if (selectedProductId !== null) {
      fetchPrices(selectedProductId);
    }
  }, [selectedProductId]);

  const fetchPrices = async (productId: number) => {
    try {
      const response = await fetch(`http://localhost:5000/product-prices/${productId}`);
      const data: Price[] = await response.json();
      setPrices(data);
    } catch (error) {
      console.error('Error fetching prices:', error);
    }
  };

  const calculatePriceDifference = (basePrice: number, price: number) => {
    const difference = ((price - basePrice) / basePrice) * 100;
    return `${difference.toFixed(2)}%`;
  };

  const sortedPrices = prices.sort((a, b) => a.price - b.price);

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-12">
          <table className="table table-dark">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Store</th>
                <th scope="col">Price</th>
                <th scope="col">Price Difference</th>
                <th scope="col">Link</th>
              </tr>
            </thead>
            <tbody>
              {sortedPrices.map((price, index) => (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td>{price.store_name}</td>
                  <td>{price.price} ₽</td>
                  <td>
                    {index === 0
                      ? 'Base Price'
                      : calculatePriceDifference(sortedPrices[0].price, price.price)}
                  </td>
                  <td>
                    <a href={price.link} target="_blank" rel="noopener noreferrer">Link</a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Pricecomp;
