import React, { useState } from 'react';
import Favorites from './Favorites'; 
import LoginModal from './LoginModal';
import RegisterModal from './RegisterModal';
import 'bootstrap/dist/css/bootstrap.min.css';

interface FavoriteProduct {
  id: number;
  name: string;
  description: string;
  image_url: string;
}

interface HeaderProps {
  setLoggedInUser: React.Dispatch<React.SetStateAction<string | null>>;
  userId: number | null;
  onSelectProduct: (product: FavoriteProduct) => void; // Add this prop
}

const Header: React.FC<HeaderProps> = ({ setLoggedInUser, userId, onSelectProduct }) => {
  const [showFavorites, setShowFavorites] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [showRegisterModal, setShowRegisterModal] = useState(false);
  const [loggedInUser, setLoggedInUserState] = useState<string | null>(null);

  const handleToggleFavorites = () => setShowFavorites(!showFavorites);
  const handleShowLogin = () => setShowLoginModal(true);
  const handleShowRegister = () => setShowRegisterModal(true);
  const handleCloseLogin = () => setShowLoginModal(false);
  const handleCloseRegister = () => setShowRegisterModal(false);

  const handleLoginSuccess = (username: string) => {
    setLoggedInUserState(username);
    setLoggedInUser(username);
    handleCloseLogin();
  };

  const handleLogout = () => {
    setLoggedInUserState(null);
    setLoggedInUser(null);
  };

  return (
    <div>
      <header className="fixed-top bg-dark text-white p-3">
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-dark">
            <a className="navbar-brand navttext" href="#top">PriceComparison</a>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
              <ul className="navbar-nav">
                {loggedInUser && (
                  <li className="nav-item me-3">
                    <button
                      onClick={handleToggleFavorites}
                      className="btn btn-secondary"
                    >
                      {showFavorites ? 'Спрятать избранное' : 'Показать избранное'}
                    </button>
                  </li>
                )}
                {loggedInUser ? (
                  <>
                    <li className="nav-item me-3">
                      <span>{loggedInUser}</span>
                    </li>
                    <li className="nav-item">
                      <button
                        onClick={handleLogout}
                        className="btn btn-danger"
                      >
                        Выйти
                      </button>
                    </li>
                  </>
                ) : (
                  <>
                    <li className="nav-item me-3">
                      <button
                        onClick={handleShowRegister}
                        className="btn btn-primary"
                      >
                        Зарегистрироваться
                      </button>
                    </li>
                    <li className="nav-item">
                      <button
                        onClick={handleShowLogin}
                        className="btn btn-success"
                      >
                        Войти
                      </button>
                    </li>
                  </>
                )}
              </ul>
            </div>
          </nav>
        </div>
      </header>

      {/* Show Favorites on the right side when toggled */}
      {showFavorites && <Favorites userId={userId} onSelectProduct={onSelectProduct} />}  {/* Pass onSelectProduct */}

      <LoginModal
        show={showLoginModal}
        onClose={handleCloseLogin}
        onLoginSuccess={handleLoginSuccess}
      />
      <RegisterModal
        show={showRegisterModal}
        onClose={handleCloseRegister}
      />
    </div>
  );
};

export default Header;
