import React from 'react';

interface Product {
  id: number;
  name: string;
  description: string;
  image_url: string;
}

interface ContentProps {
  selectedProduct: Product | null;
  userId: number | null; // Add userId to the ContentProps interface
}

const Content: React.FC<ContentProps> = ({ selectedProduct, userId }) => {
  const handleAddToFavorites = async () => {
    if (!userId) {
      alert('Please log in to favorite products');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/favorites', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId, productId: selectedProduct?.id }),
      });

      if (!response.ok) {
        throw new Error('Failed to add to favorites');
      }

      const data = await response.json();
      alert('Product added to favorites successfully!');
    } catch (error) {
      if (error instanceof Error) {
        alert(error.message);
      } else {
        alert('An unexpected error occurred');
      }
    }
  };

  return (
    <div className="container">
      {selectedProduct ? (
        <section id="product-details" className="my-5 b">
          <div className="container my-1">
            <div className="row justify-content-center align-items-center">
              <div className="col-md-2">
                <img
                  src={selectedProduct.image_url || 'https://placehold.co/600x400'}
                  loading="lazy"
                  alt={selectedProduct.name}
                  className="img-fluid"
                />
              </div>
              <div className="col-md-6">
                <article>
                  <h2>{selectedProduct.name}</h2>
                  <p>{selectedProduct.description}</p>
                  <button onClick={handleAddToFavorites} className="btn btn-primary">
                    Add to Favorites
                  </button>
                </article>
              </div>
            </div>
          </div>
        </section>
      ) : (
        <div className="row d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
          <div className="col-md-6 text-center">
            <p>Please select a product to view details.</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Content;
