import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

interface SearchResult {
  id: number;
  name: string;
}

interface SearchbarProps {
  onSelect: (productId: number, productName: string) => void; // Add this prop to pass selected product
}

const Searchbar: React.FC<SearchbarProps> = ({ onSelect }) => {
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);

  const handleSearch = async () => {
    if (!searchTerm) return;

    try {
      const response = await fetch(`http://localhost:5000/search?q=${searchTerm}`);
      const data: SearchResult[] = await response.json();
      setSearchResults(data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  const handleSelect = (product: SearchResult) => {
    onSelect(product.id, product.name);
    setSearchResults([]); // Clear the results after selection
    setSearchTerm(''); // Reset the search bar after selecting
  };

  return (
    <div className="searchbar">
      <section id="searchsect" className="my-5 b">
        <div className="container my-1">
          <div className="row">
            <div className="col-md-9">
              <div className="input-group">
                <input
                  type="search"
                  className="form-control rounded"
                  placeholder="Search"
                  aria-label="Search"
                  aria-describedby="search-addon"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button
                  type="button"
                  className="btn btn-outline-primary"
                  onClick={handleSearch}
                >
                  Search
                </button>
              </div>
              {searchResults.length > 0 && (
                <ul className="list-group">
                  {searchResults.map((result) => (
                    <li
                      key={result.id}
                      className="list-group-item"
                      onClick={() => handleSelect(result)}
                    >
                      {result.name}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Searchbar;
