import React, { useState } from 'react';
import Favorites from './Favourites';
import LoginModal from './LoginModal';
import RegisterModal from './RegisterModal';
import 'bootstrap/dist/css/bootstrap.min.css';

interface HeaderProps {
  setLoggedInUser: React.Dispatch<React.SetStateAction<string | null>>;
  userId: number | null; // Pass userId to show favorites
}

const Header: React.FC<HeaderProps> = ({ setLoggedInUser, userId }) => {
  const [showFavorites, setShowFavorites] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [showRegisterModal, setShowRegisterModal] = useState(false);
  const [loggedInUser, setLoggedInUserState] = useState<string | null>(null);

  const handleToggleFavorites = () => setShowFavorites(!showFavorites);
  const handleShowLogin = () => setShowLoginModal(true);
  const handleShowRegister = () => setShowRegisterModal(true);
  const handleCloseLogin = () => setShowLoginModal(false);
  const handleCloseRegister = () => setShowRegisterModal(false);

  const handleLoginSuccess = (username: string) => {
    setLoggedInUserState(username);
    setLoggedInUser(username);
    handleCloseLogin();
  };

  const handleLogout = () => {
    setLoggedInUserState(null);
    setLoggedInUser(null);
  };

  return (
    <div>
      <header className="fixed-top bg-dark text-white p-3">
        <div className="container">
          <nav className="navbar navbar-expand-lg navbar-dark">
            <a className="navbar-brand navttext" href="#top">PriceComparison</a>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse justify-content-end" id="navbarNav">
              <ul className="navbar-nav">
                {loggedInUser && (
                  <li>
                    <button onClick={handleToggleFavorites}>
                      {showFavorites ? 'Спрятать избранное' : 'Показать избранное'}
                    </button>
                  </li>
                )}
                {loggedInUser ? (
                  <>
                    <li className="nav-item text-white me-3">
                      <span>{loggedInUser}</span>
                    </li>
                    <li>
                      <button onClick={handleLogout} className="btn btn-danger">Выйти</button>
                    </li>
                  </>
                ) : (
                  <>
                    <li>
                      <button onClick={handleShowRegister}>Зарегистрироваться</button>
                    </li>
                    <li>
                      <button onClick={handleShowLogin}>Войти</button>
                    </li>
                  </>
                )}
              </ul>
            </div>
          </nav>
        </div>
      </header>

      {/* Show Favorites on the right side when toggled */}
      {showFavorites && <Favourites userId={userId} />}

      <LoginModal show={showLoginModal} onClose={handleCloseLogin} onLoginSuccess={handleLoginSuccess} />
      <RegisterModal show={showRegisterModal} onClose={handleCloseRegister} />
    </div>
  );
};

export default Header;
