import React, { useState } from 'react';

interface Product {
  id: number;
  name: string;
  description: string;
  image_url: string;
}

interface ContentProps {
  selectedProduct: Product | null;
  userId: number | null;
}

const Content: React.FC<ContentProps> = ({ selectedProduct, userId }) => {
  const [targetPrice, setTargetPrice] = useState('');

  const handleSetPriceNotification = async () => {
    if (!userId || !selectedProduct) {
      alert('Please log in and select a product');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/notifications', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          userId,
          productId: selectedProduct.id,
          targetPrice: parseFloat(targetPrice),
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to set price notification');
      }

      const data = await response.json();
      alert('Notification set successfully');
    } catch (error) {
      console.error('Error setting price notification:', error);
      alert('Failed to set price notification');
    }
  };

  return (
    <div className="container">
      {selectedProduct ? (
        <section id="product-details" className="my-5 b">
          <div className="container my-1">
            <div className="row justify-content-center align-items-center">
              <div className="col-md-2">
                <img
                  src={selectedProduct.image_url || 'https://placehold.co/600x400'}
                  loading="lazy"
                  alt={selectedProduct.name}
                  className="img-fluid"
                />
              </div>
              <div className="col-md-6">
                <article>
                  <h2>{selectedProduct.name}</h2>
                  <p>{selectedProduct.description}</p>
                  {/* Add Target Price Input and Button */}
                  <div>
                    <label htmlFor="targetPrice">Notify me when price is below: </label>
                    <input
                      type="number"
                      id="targetPrice"
                      value={targetPrice}
                      onChange={(e) => setTargetPrice(e.target.value)}
                      min="0"
                      step="0.01"
                      className="form-control"
                    />
                    <button onClick={handleSetPriceNotification} className="btn btn-primary mt-2">
                      Set Price Notification
                    </button>
                  </div>
                </article>
              </div>
            </div>
          </div>
        </section>
      ) : (
        <div className="row d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
          <div className="col-md-6 text-center">
            <p>Please select a product to view details.</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Content;
