import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Searchbar from './components/Searchbar';
import Content from './components/Content';
import Pricecomp from './components/Pricecomp';

function App() {
  const [selectedProductId, setSelectedProductId] = useState<number | null>(null);
  const [selectedProductName, setSelectedProductName] = useState<string | null>(null);

  const handleProductSelect = (productId: number, productName: string) => {
    setSelectedProductId(productId);
    setSelectedProductName(productName);
  };

  return (
    <div className="App">
      <Header />
      <main>
        {/* Pass the product selection handler to the Searchbar */}
        <Searchbar onSelect={handleProductSelect} />

        {/* Pass the selected product ID and name to Content */}
        <Content selectedProductId={selectedProductId} selectedProductName={selectedProductName} />

        {/* Pass the selected product ID to Pricecomp */}
        <Pricecomp selectedProductId={selectedProductId} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
