import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

interface LoginModalProps {
  show: boolean;
  onClose: () => void;
}

const LoginModal: React.FC<LoginModalProps> = ({ show, onClose }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);  // For loading state
  const [errorMessage, setErrorMessage] = useState<string | null>(null);  // For error handling

  const handleLogin = async () => {
    setLoading(true); // Start loading
    setErrorMessage(null); // Clear any previous errors

    const loginData = { username, password };

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      if (!response.ok) {
        const errorData = await response.json();  // Extract error message from server
        throw new Error(errorData.message || 'Login failed');
      }

      const data = await response.json();
      console.log('Login success:', data);
      onClose(); // Close the modal after successful login
    } catch (error) {
      if (error instanceof Error) {
        setErrorMessage(error.message);  // Set error message to display
      } else {
        setErrorMessage('An unexpected error occurred');
      }
    } finally {
      setLoading(false); // Stop loading
    }
  };

  if (!show) return null;

  return (
    <div className="modal fade show d-block" tabIndex={-1} role="dialog" aria-labelledby="loginModalLabel" aria-hidden="true">
      <div className="modal-dialog" role="document">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="loginModalLabel">Login</h5>
            <button type="button" className="btn-close" onClick={onClose} aria-label="Close"></button>
          </div>
          <div className="modal-body">
            {/* Display error message */}
            {errorMessage && <div className="alert alert-danger">{errorMessage}</div>}

            <input
              type="text"
              className="form-control mb-3"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              disabled={loading}  // Disable input while loading
            />
            <input
              type="password"
              className="form-control"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              disabled={loading}  // Disable input while loading
            />
          </div>
          <div className="modal-footer">
            <button
              type="button"
              className="btn btn-secondary"
              onClick={onClose}
              disabled={loading}  // Disable button while loading
            >
              Close
            </button>
            <button
              type="button"
              className="btn btn-primary"
              onClick={handleLogin}
              disabled={loading}  // Disable button while loading
            >
              {loading ? 'Logging in...' : 'Login'}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LoginModal;
