import React, { useState, useEffect } from 'react';

interface Price {
  store_name: string;
  price: number;
}

interface PricecompProps {
  selectedProductId: number | null; // Accept the selected product ID as a prop
  selectedProductName: string | null;
}

const Pricecomp: React.FC<PricecompProps> = ({ selectedProductId, selectedProductName }) => {
  const [prices, setPrices] = useState<Price[]>([]);

  useEffect(() => {
    const fetchPrices = async () => {
      if (selectedProductId) {
        try {
          const response = await fetch(`http://localhost:5000/prices/${selectedProductId}`);
          const data = await response.json();
          setPrices(data);
        } catch (error) {
          console.error('Error fetching prices:', error);
        }
      }
    };

    fetchPrices();
  }, [selectedProductId]); // Trigger fetching prices when the selected product changes

  // Find the cheapest price for comparison
  const cheapestPrice = prices.length > 0 ? prices[0].price : null;

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-12">
          <table className="table table-dark">
            <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">Name</th>
                <th scope="col">Shop</th>
                <th scope="col">Price</th>
                <th scope="col">Price Difference (%)</th>
              </tr>
            </thead>
            <tbody>
              {prices.map((price, index) => (
                <tr key={index}>
                  <th scope="row">{index + 1}</th>
                  <td>{selectedProductName}</td> {/* Use the selected product name */}
                  <td>{price.store_name}</td>
                  <td>{price.price} ₽</td>
                  <td>
                    {cheapestPrice && price.price !== cheapestPrice
                      ? `${(((price.price - cheapestPrice) / cheapestPrice) * 100).toFixed(2)}%`
                      : 'Cheapest'}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default Pricecomp;
