import React, { useState } from 'react';
import Header from "./components/Header";
import Footer from "./components/Footer";
import Searchbar from "./components/Searchbar";
import Content from "./components/Content";
import Pricecomp from "./components/Pricecomp";

interface Product {
  id: number;
  name: string;
  description: string;
  image_url: string;
}

function App() {
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [loggedInUser, setLoggedInUser] = useState<string | null>(null);

  // Handle product selection and pass it to the components that need it
  const handleProductSelect = (product: Product) => {
    setSelectedProduct(product);
  };

  const userId = loggedInUser ? 1 : null; // Simulating a user ID

  return (
    <div className="App">
      {/* Pass handleProductSelect as onSelectProduct to the Header */}
      <Header 
        setLoggedInUser={setLoggedInUser} 
        userId={userId} 
        onSelectProduct={handleProductSelect} // Add this prop
      />
      <main>
        <Searchbar onSelect={handleProductSelect} />
        <Content selectedProduct={selectedProduct} userId={userId} />
        <Pricecomp productId={selectedProduct?.id || null} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
