import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

interface SearchResult {
  column_name: string; // Adjust this according to the actual column names returned from the database
}

const Searchbar: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState<string>(''); // Type annotation for the search term
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]); // Type annotation for search results (array of SearchResult)

  const handleSearch = async (): Promise<void> => {
    if (!searchTerm) return;

    try {
      const response = await fetch(`http://localhost:5000/search?q=${searchTerm}`);
      const data: SearchResult[] = await response.json(); // Type annotation for fetched data
      setSearchResults(data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  return (
    <div className="searchbar">
      <section id="searchsect" className="my-5 b">
        <div className="container my-1">
          <div className="row">
            <div className="col-md-9">
              <div className="input-group">
                <input
                  type="search"
                  className="form-control rounded"
                  placeholder="Search"
                  aria-label="Search"
                  aria-describedby="search-addon"
                  value={searchTerm}
                  onChange={(e: React.ChangeEvent<HTMLInputElement>) => setSearchTerm(e.target.value)} // Typed event
                />
                <button
                  type="button"
                  className="btn btn-outline-primary"
                  onClick={handleSearch}
                >
                  Search
                </button>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-md-12">
              <ul>
                {searchResults.map((result, index) => (
                  <li key={index}>{result.column_name}</li> // Adjust `column_name` based on your actual database structure
                ))}
              </ul>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Searchbar;
