import React, { useState } from 'react';
import Header from "./components/Header";
import Footer from "./components/Footer";
import Searchbar from "./components/Searchbar";
import Content from "./components/Content";
import Pricecomp from "./components/Pricecomp";

interface Product {
  id: number;
  name: string;
  description: string;
  image_url: string;
}

interface User {
  id: number;
  username: string;
}

function App() {
  const [selectedProduct, setSelectedProduct] = useState<Product | null>(null);
  const [loggedInUser, setLoggedInUser] = useState<User | null>(null); // Add loggedInUser state

  const handleProductSelect = (product: Product) => {
    setSelectedProduct(product); // Update the selected product state
  };

  const handleLoginSuccess = (user: User) => {
    setLoggedInUser(user); // Update the loggedInUser state on successful login
  };

  return (
    <div className="App">
      <Header />
      <main>
        {/* Pass the handleProductSelect function as a prop to Searchbar */}
        <Searchbar onSelect={handleProductSelect} />
        {/* Pass the selectedProduct and loggedInUser to Content */}
        <Content selectedProduct={selectedProduct} loggedInUser={loggedInUser} />
        {/* Pass the selectedProduct's ID to Pricecomp */}
        <Pricecomp productId={selectedProduct?.id || null} />
      </main>
      <Footer />
    </div>
  );
}

export default App;
